// --- Pub/Sub for reactive updates ---
type Listener = () => void;
const listeners: Listener[] = [];

const subscribe = (listener: Listener) => {
    listeners.push(listener);
    return () => {
        const index = listeners.indexOf(listener);
        if (index > -1) listeners.splice(index, 1);
    };
};

const notify = () => {
    listeners.forEach(listener => listener());
};

// --- Gamification Logic ---
const addPoints = (userId: string, points: number) => {
    const user = users.find(u => u.id === userId);
    if (user) user.points += points;
};

// --- Data Mutation Functions ---
const addSapling = (data: { species: string; location: { lat: number; lng: number }; guardianId: string; image: File; recommendation?: string; }) => {
    const newId = `VRIKSHA-${100 + saplings.length + 1}`;
    const today = new Date().toISOString().split('T')[0];
    const weather = getMockWeatherData(data.location.lat, data.location.lng);
    const soil = getSoilCondition(weather);

    const newSapling: Sapling = {
        id: newId,
        species: data.species,
        location: data.location,
        guardianId: data.guardianId,
        plantationDate: today,
        updates: [{
            id: `update-${Math.random().toString(36).substr(2, 9)}`,
            date: today,
            status: HealthStatus.HEALTHY,
            imageUrl: URL.createObjectURL(data.image),
            submittedBy: data.guardianId,
            recommendation: data.recommendation,
            confidence: 0.98,
            weather: weather,
            soilCondition: soil,
        }],
    };
    saplings.unshift(newSapling);
    addPoints(data.guardianId, 20); // Points for registering
    notify();
    return newSapling;
};

const addSaplingUpdate = (saplingId: string, data: { status: HealthStatus; image: File; userId: string; recommendation?: string; confidence?: number; weather: WeatherData; soil: string }) => {
    const sapling = saplings.find(s => s.id === saplingId);
    if (!sapling) return;

    const previousStatus = sapling.updates[sapling.updates.length - 1]?.status;

    const newUpdate: SaplingUpdate = {
        id: `update-${Math.random().toString(36).substr(2, 9)}`,
        date: new Date().toISOString().split('T')[0],
        status: data.status,
        imageUrl: URL.createObjectURL(data.image),
        submittedBy: data.userId,
        recommendation: data.recommendation,
        confidence: data.confidence,
        weather: data.weather,
        soilCondition: data.soil,
    };
    sapling.updates.push(newUpdate);
    
    // Gamification points
    if (data.status === HealthStatus.HEALTHY) {
        addPoints(data.userId, 10);
        // Bonus for fixing a problem
        if (previousStatus === HealthStatus.NEEDS_WATER || previousStatus === HealthStatus.DAMAGED) {
            addPoints(data.userId, 20); 
        }
    }
    
    notify();
};

const deleteSapling = (saplingId: string) => {
    const index = saplings.findIndex(s => s.id === saplingId);
    if (index > -1) {
        saplings.splice(index, 1);
        notify();
    }
};

const createSocialPost = (data: { userId: string, caption: string, image: File, saplingId?: string }) => {
    const newPost: SocialPost = {
        id: `post-${Date.now()}`,
        userId: data.userId,
        caption: data.caption,
        imageUrl: URL.createObjectURL(data.image),
        saplingId: data.saplingId,
        timestamp: new Date().toISOString(),
        likes: [],
        comments: []
    };
    socialPosts.unshift(newPost);
    addPoints(data.userId, 15);
    notify();
};

const toggleLike = (postId: string, userId: string) => {
    const post = socialPosts.find(p => p.id === postId);
    if (!post) return;
    const likeIndex = post.likes.findIndex(l => l.userId === userId);
    if (likeIndex > -1) {
        post.likes.splice(likeIndex, 1);
        addPoints(post.userId, -2);
    } else {
        post.likes.push({ userId });
        addPoints(post.userId, 2);
    }
    notify();
};

const addComment = (postId: string, userId: string, text: string) => {
    const post = socialPosts.find(p => p.id === postId);
    if (!post) return;
    const newComment: Comment = {
        id: `comment-${Date.now()}`,
        userId, text, timestamp: new Date().toISOString()
    };
    post.comments.push(newComment);
    addPoints(userId, 5);
    addPoints(post.userId, 3);
    notify();
};


// --- Leaderboard and Analytics Helpers ---
export type UserWithBadges = User & { badges: string[] };
const getLeaderboardWithLevels = (): UserWithBadges[] => {
    const volunteers = users
        .filter(u => u.role === Role.VOLUNTEER)
        .sort((a, b) => b.points - a.points);
    
    const topScorerId = volunteers.length > 0 ? volunteers[0].id : null;

    return volunteers.map((user, index) => {
        let level = 'ðŸŒ¿ Green Novice';
        if (user.points > 200) level = 'ðŸŒ³ Forest Hero';
        else if (user.points > 100) level = 'ðŸŒ² Eco Guardian';

        const badges: string[] = [];
        if (index === 0) badges.push('top_rank');
        if (user.id === topScorerId) badges.push('high_scorer');

        return { ...user, rank: index + 1, level, badges };
    });
};

export interface AppNotification {
    id: string;
    message: string;
    type: 'info' | 'warning' | 'success';
}

const getNotificationsForUser = (userId: string): AppNotification[] => {
    const userSaplings = saplings.filter(s => s.guardianId === userId);
    const notifications: AppNotification[] = [];

    const saplingsNeedingUpdate = userSaplings.filter(s => {
        const lastUpdate = new Date(s.updates[s.updates.length - 1].date);
        const daysSinceUpdate = (new Date().getTime() - lastUpdate.getTime()) / (1000 * 3600 * 24);
        return daysSinceUpdate > 20 && s.updates[s.updates.length - 1].status !== HealthStatus.LOST;
    });

    if (saplingsNeedingUpdate.length > 0) {
        notifications.push({
            id: 'update-reminder',
            type: 'warning',
            message: `Don't forget! ${saplingsNeedingUpdate.length} of your saplings haven't been updated in over 20 days. ðŸ’§`,
        });
    }

    // Check for growth trend
    const recentlyImproved = userSaplings.find(s => {
        if (s.updates.length < 2) return false;
        const lastUpdate = s.updates[s.updates.length-1];
        const lastStatus = lastUpdate.status;
        const prevStatus = s.updates[s.updates.length - 2].status;
         const daysSinceUpdate = (new Date().getTime() - new Date(lastUpdate.date).getTime()) / (1000 * 3600 * 24);

        return daysSinceUpdate <= 10 && lastStatus === HealthStatus.HEALTHY && (prevStatus === HealthStatus.NEEDS_WATER || prevStatus === HealthStatus.DAMAGED);
    });

    if (recentlyImproved) {
         notifications.push({
            id: `improved-${recentlyImproved.id}`,
            type: 'success',
            message: `Excellent care! Your sapling ${recentlyImproved.id}'s growth rate improved +15% this month! ðŸŒ¿`,
        });
    }

    return notifications;
};


